.rank-wrap,
.pgc-rank-wrap,
.manga-rank-item,
.rank-wrap >a>p,.rank-wrap .preview .txt >a>p,
.pgc-rank-wrap >a>p,.pgc-rank-wrap .preview .txt >a>p,
.manga-rank-item >a>p,.manga-rank-item .preview .preview-desc .title{
    transition: all 0.3s,border-bottom 0.5s;
}
.rank-wrap,
.pgc-rank-wrap,
.manga-rank-item{
    position:relative;
}
