    padding-bottom:0;
    margin-bottom:9px;
}
.rank-wrap:hover,
.pgc-rank-wrap:hover,
.manga-rank-item:hover{
    background: var(--boxbacks,rgba(255, 255, 255, 0.85));
    /*box-shadow: 5px 5px 10px 10px rgba(255, 255, 255, .1);*/
    padding-left:6px;
    margin-right:-6px;
/*     margin-top:-2px; */
}
.rank-wrap >a:after,.rank-wrap .preview .txt >a:after,
.pgc-rank-wrap >a:after,.pgc-rank-wrap .preview .txt >a:after,
.manga-rank-item >a .title:after,.manga-rank-item .preview .preview-desc .title:after{
    content: "";display: block;position: absolute;height:100%;width: 0;top: 0;left:100%;
    transition: all .45s var(--v1);opacity: .5;/*clear:both;*/
    border-bottom:0.1rem solid ;/*直接画线会导致块变大qwq，所以提前画好，透明就看不见了*/
    /*animation:toright .75s ease 0s 1;视觉效果是底部线向右收缩*/
}
.rank-wrap:hover >a:after,.rank-wrap:hover .preview .txt >a:after,
.pgc-rank-wrap:hover >a:after,.pgc-rank-wrap:hover .preview .txt >a:after,
.manga-rank-item:hover >a .title:after,.manga-rank-item:hover .preview .preview-desc .title:after{
    width:100%;opacity: 1;left: 0;
    transition:all .45s var(--v1), left 0s;/*时间曲线来源于https://easings.net/#easeOutCubic*/
    /*animation:fromleft .75s ease 0s 1;视觉效果是从左出现底部线*/
}
